		List alist = new ArrayList()
		for (Object o : mixedList) {
			if (o instanceof A) {
				A a = (A) o
				alist.add(kc)
			}
		}
		Iterator it = alist.iterator()
		while(it.hasNext()) {
			a = it.next() // there is no variable 'a' in this scope the Create local variable hint is enabled here <<<<<<<<<<<<<
		}
