public void method() {
	List mixedList =getMyList()
	if (selection.size() >= 2) {
		// make a pruned list of 'A's
		List alist = new ArrayList()
		for (Object o : mixedList) {
			if (o instanceof A) {
				A a = (A) o // This line is still valid <<<<<<<<<<<<<<<<<<<<<<<
				alist.add(kc)
			}
		}
		Iterator it = alist.iterator()
		while(it.hasNext()) {
			A a = it.next() // Now the variable 'a' is declared and we don't disturb any other code. It is also initialized and ready to go so I can finish coding the while loop
		}
	}
}

